parameters.inputSchema:
{
  "$schema": "https://json-schema.org/draft/2020-12/schema",
  "type": "object",
  "description": "Schema for extracted resume data from a PDF",
  "properties": {
    "full_name": {
      "type": "string",
      "description": "The full name of the candidate as extracted from the resume."
    },
    "job_position": {
      "type": "string",
      "description": "Candidate's job position."
    },
    "email_address": {
      "type": "string",
      "format": "email",
      "description": "Candidate's email address."
    },
    "phone_number": {
      "type": "string",
      "description": "Candidate's phone number, including country code if available."
    },
    "address": {
      "type": "string",
      "description": "Candidate's address as listed on the resume."
    },
    "summary": {
      "type": "string",
      "description": "A brief summary of the candidate's experience and expertise."
    },
    "experience": {
      "type": "string",
      "description": "Comma-separated list of work experiences in the format: 'Job Title at Company (Start Date - End Date)'"
    },
    "education": {
      "type": "string",
      "description": "Comma-separated list of educational background in the format: 'Degree from School (Graduation Year)'"
    },
    "skills": {
      "type": "string",
      "description": "Comma-separated list of technical and soft skills."
    },
     "language": {
      "type": "string",
      "description": "Comma-separated list of known language.(default English)"
    },
    "certifications": {
      "type": "string",
      "description": "Comma-separated list of certifications in the format: 'Certification (Issuer, Date)'"
    },
    "social_links": {
      "type": "string",
      "description": "Comma-separated list of social media or professional profile links (e.g., LinkedIn, GitHub, Portfolio)."
    }
  },
  "required": ["full_name", "email_address", "phone_number", "language"]
}

parameters.jsCode:
let commandMessage = "";
for (const item of $input.all()) {
  commandMessage += `${item.json.command} - ${item.json.description} \n`
}
return [{json: {message: commandMessage}}];

